import React from 'react';

class Form extends React.Component {
state = {
  name: '',
  email: '',
  password: ''
}

  render() {

    return (
      <section id="form-section">
      <div className="sign-up-form">
      <h3>SIGN UP FOR FREE</h3>
      <form onSubmit={this.handleSubmit}>
            <input type="text" placeholder="Name" value={this.state.name} />
            <input type="text" placeholder="E-mail" value={this.state.email} />
            <input type="text" placeholder="Password" value={this.state.password} />
      </form>
        <input type="submit" value="Sign up" onClick={this.handleChange}  />
      <p>By clicking "Sign up" I agree to our</p>
      <a href="#">Terms of Service</a>
      </div>
</section>
    );
  }
}

export default Form;
